class Environment:
    """Message bus with deterministic delivery (once per role), routing, and artifact lookup."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self._delivered: Dict[str, Set[str]] = {}
        self._by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._delivered.setdefault(role.name, set())
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # ensure id exists and send_to normalized
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        if getattr(message, "send_to", None) is None:
            try:
                message.send_to = set(getattr(message, "sent_to", set()) or set())
            except Exception:
                message.send_to = set()
        self.history.append(message)
        if getattr(message, "cause_by", None):
            self._by_cause.setdefault(message.cause_by, []).append(message)
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"Msg {message.id} from {message.sent_from} cause_by={message.cause_by} -> {list(getattr(message,'send_to',set()))} preview={preview}")

    def has_been_delivered(self, role: Role, message: Message) -> bool:
        return getattr(message, "id", None) in self._delivered.get(role.name, set())

    def mark_delivered(self, role: Role, message: Message):
        if getattr(message, "id", None):
            self._delivered.setdefault(role.name, set()).add(message.id)
            if self.tracer:
                self.tracer.log("ENV_MARK_DELIVERED", "Environment", f"{role.name} delivered {message.id}")

    def get_messages_for_role(self, role: Role) -> List[Message]:
        out: List[Message] = []
        delivered = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid or mid in delivered:
                continue
            if getattr(msg, "sent_from", None) == role.name:
                delivered.add(mid)
                continue
            # explicit routing
            targets = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None) or set()
            targeted = False
            try:
                if isinstance(targets, (set, list)) and (role.name in targets or role.profile in targets or "*" in targets):
                    targeted = True
                elif isinstance(targets, str) and targets in (role.name, role.profile, "*"):
                    targeted = True
            except Exception:
                targeted = False
            if targeted:
                out.append(msg)
                delivered.add(mid)
                continue
            # watch trigger
            if getattr(msg, "cause_by", None) in role.watch_list:
                out.append(msg)
                delivered.add(mid)
                continue
            # default: user input to coder(s)
            if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile:
                out.append(msg)
                delivered.add(mid)
                continue
        return out

    def get_latest_artifact(self, cause_names: Iterable[str]) -> Optional[Message]:
        cause_set = set(cause_names)
        for m in reversed(self.history):
            if getattr(m, "cause_by", None) in cause_set:
                return m
        return None


class Team:
    """Deterministic orchestrator with robust progress tracking and stable verification termination."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # verification streak tracking
        self._pass_streak: int = 0
        self._required_stable: int = VERIFY_REQUIRED_STREAK
        self._last_digest: Optional[str] = None

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _confirm_verification(self) -> bool:
        """Local confirmation to avoid premature termination."""
        verifier = SimpleVerify(context=self.context)
        code_msg = self.env.get_latest_artifact([SimpleWriteCode.name])
        tests_msg = self.env.get_latest_artifact([SimpleWriteTest.name])
        result = await verifier.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
        return isinstance(result, str) and "VERIFICATION_RESULT: PASS" in result

    async def _process_role(self, role: Role) -> int:
        produced = 0
        for msg in self.env.get_messages_for_role(role):
            try:
                resp = await role.act(msg)
            except Exception as e:
                self.tracer.log("ROLE_RUN_ERROR", role.name, f"{type(e).__name__}: {str(e)[:160]}")
                resp = Message(
                    content=f"ROLE_EXCEPTION: {type(e).__name__}: {str(e)[:160]}",
                    role=role.profile,
                    cause_by="RoleRunError",
                    sent_from=role.name,
                    send_to={"Team"}
                )
            # mark delivered regardless of success
            self.env.mark_delivered(role, msg)
            if resp:
                self.env.publish_message(resp)
                produced += 1
                # verification streak update
                if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                    content = resp.content
                    if "VERIFICATION_RESULT: PASS" in content:
                        # confirm PASS deterministically
                        confirmed = await self._confirm_verification()
                        if not confirmed:
                            if self._pass_streak:
                                self.tracer.log("VERIFY_RESET", "Team", f"confirmation_failed; reset {self._pass_streak}->0")
                            self._pass_streak = 0
                            self._last_digest = None
                        else:
                            digest = None
                            for part in content.split("|"):
                                p = part.strip()
                                if p.startswith("digest="):
                                    digest = p.split("=", 1)[1]
                                    break
                            if digest and digest == self._last_digest:
                                self._pass_streak += 1
                            else:
                                self._last_digest = digest
                                self._pass_streak = 1
                            self.tracer.log("VERIFY_PASS", "Team", f"digest={digest} streak={self._pass_streak}")
                    else:
                        if self._pass_streak:
                            self.tracer.log("VERIFY_RESET", "Team", f"reset {self._pass_streak}->0")
                        self._pass_streak = 0
                        self._last_digest = None
        return produced

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds; need {self._required_stable} stable passes")
        # initial message directed to coder
        initial = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to={"SimpleCoder"}
        )
        self.env.publish_message(initial)

        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        last_history_len = len(self.env.history)
        no_progress_rounds = 0
        round_num = 0

        for r in range(1, n_round + 1):
            round_num = r
            self.tracer.log("ROUND_START", "Team", f"Round {r}/{n_round}")
            new_msgs = 0
            for RoleClass in order:
                for role in [x for x in self.env.roles if isinstance(x, RoleClass)]:
                    new_msgs += await self._process_role(role)

            # progress/stall handling
            if len(self.env.history) == last_history_len:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0
            last_history_len = len(self.env.history)

            self.tracer.log("ROUND_END", "Team", f"round={r} produced={new_msgs} streak={self._pass_streak} history={last_history_len}")

            # termination after stable verification
            if self._pass_streak >= self._required_stable:
                self.tracer.log("TEAM_EARLY_STOP", "Team", f"Stable verification achieved (streak={self._pass_streak}); stopping")
                break

            # gentle nudge on first stall; terminate on prolonged stall
            if new_msgs == 0:
                if no_progress_rounds >= NO_PROGRESS_GRACE:
                    self.tracer.log("TEAM_NO_PROGRESS", "Team", f"No progress for {no_progress_rounds} rounds; terminating")
                    break
                nudge = Message(
                    content=f"Nudge: please refine implementation for '{self.idea}'.",
                    instruct_content=self.idea,
                    role="System",
                    sent_from="Orchestrator",
                    cause_by="Nudge",
                    send_to={"SimpleCoder"}
                )
                self.env.publish_message(nudge)
                # immediately process coder nudge
                for coder in [x for x in self.env.roles if isinstance(x, SimpleCoder)]:
                    await self._process_role(coder)

            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run finished")
        summary = f"Project '{self.idea}' finished after {round_num} rounds with {len(self.env.history)} messages. Verified={(self._pass_streak >= self._required_stable)}"
        self.tracer.log("SUMMARY", "Team", summary)

