import asyncio
import ast
import hashlib
import random
from typing import Tuple, Iterable

# Core principles:
# - Single-responsibility roles with clear watch/trigger logic
# - Explicit routing via send_to and idempotent message delivery
# - Robust LLM/API error handling and retries with exponential backoff + jitter
# - Deterministic verification and stable termination requiring consecutive passes
# - Simplified orchestration and readable code paths

LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
ROLE_MAX_ATTEMPTS = 2
REQUIRED_STABLE_PASSES = 2
NO_PROGRESS_LIMIT = 2  # consecutive rounds without progress before considering stall


class Action(ABC):
    """Base action with robust LLM wrapper and clear run contract."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _ask_llm(self, messages: List[Dict[str, str]]) -> str:
        """Ask LLM with retries/backoff. Returns string or 'LLM_ERROR:<reason>'."""
        tracer = getattr(self.context, "tracer", None)
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLM unavailable; using deterministic fallback")
            return "LLM_ERROR:unavailable"

        last_err = None
        for attempt in range(1, LLM_MAX_RETRIES + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{LLM_MAX_RETRIES}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty response")
                low = resp.strip().lower()
                if low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError("LLM error-like response")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"len={len(resp)}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err[:160]}")
                if attempt < LLM_MAX_RETRIES:
                    backoff = LLM_BACKOFF_BASE * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.2)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Failed after retries: {last_err}")
        return f"LLM_ERROR:{last_err or 'unknown'}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError()


class SimpleWriteCode(Action):
    """Produce an initial implementation from an idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            # deterministic, parseable fallback when no idea
            return (
                "def solve(value=None):\n"
                "    \"\"\"Fallback placeholder implementation.\"\"\"\n"
                "    return value\n"
            )
        prompt = (
            "You are a professional Python developer. Return only valid Python source (no backticks). "
            "Include docstrings and input validation where appropriate. Keep it compact and testable.\n\n"
            f"Task:\n{idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt}
        ]
        code = await self._ask_llm(messages)
        if code.startswith("LLM_ERROR"):
            # deterministic fallback
            return (
                f'"""Fallback implementation for task: {idea[:200]}"""\n'
                "def solve(value=None):\n"
                "    \"\"\"Return the input (identity) as a safe fallback.\"\"\"\n"
                "    return value\n"
            )
        # Syntax validation
        try:
            ast.parse(code)
        except Exception:
            return (
                f'"""Fallback due to parse error for task: {idea[:200]}"""\n'
                "def solve(value=None):\n"
                "    return value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code)}")
        return code


class SimpleWriteTest(Action):
    """Generate pytest-style tests for a given code snippet."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        if not code or not code.strip():
            return "def test_placeholder():\n    assert True\n"
        # Extract public symbols to guide tests
        symbols: List[str] = []
        try:
            tree = ast.parse(code)
            for n in tree.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    symbols.append(n.name)
        except Exception:
            symbols = []
        prompt = (
            "You are a QA engineer. Write pytest-style tests for the provided module. "
            "Return only test code.\n"
            f"Public symbols: {', '.join(symbols[:8]) or '(none)'}\n\n"
            f"Module (truncated to 3000 chars):\n{(code or '')[:3000]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt}
        ]
        tests = await self._ask_llm(messages)
        if tests.startswith("LLM_ERROR"):
            target = (symbols[0] if symbols else "placeholder")
            return (
                "import pytest\n\n"
                f"def test_{target}_exists():\n"
                "    assert True\n"
            )
        # Validate tests contain at least one test function or assert
        try:
            parsed = ast.parse(tests)
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed)) or ("assert " in (tests or ""))
            if not (has_test_fn or has_assert):
                raise ValueError("No tests/asserts detected")
        except Exception:
            target = (symbols[0] if symbols else "placeholder")
            return (
                "import pytest\n\n"
                f"def test_{target}_sanity():\n"
                "    assert True\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests)}")
        return tests


class SimpleWriteReview(Action):
    """Review code and tests and produce actionable items."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        issues = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:120]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:120]}")

        if self.is_human:
            decision = "APPROVE" if not issues else "REQUEST_CHANGES"
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "OK") + f"\nREVIEW_DECISION: {decision}"
            if tracer:
                tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise actionable review. "
            "End with a single line 'REVIEW_DECISION: APPROVE' or 'REVIEW_DECISION: REQUEST_CHANGES'.\n\n"
            f"STATIC_ISSUES: {issues or 'none'}\n\n"
            f"Code:\n{(code or '')[:1500]}\n\nTests:\n{(tests or '')[:1500]}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt}
        ]
        resp = await self._ask_llm(messages)
        if resp.startswith("LLM_ERROR") or not isinstance(resp, str) or not resp.strip():
            return "Automated review unavailable.\nREVIEW_DECISION: REQUEST_CHANGES"
        if "REVIEW_DECISION:" not in resp:
            # infer decision heuristically
            decision = "APPROVE" if not issues else "REQUEST_CHANGES"
            resp = resp.rstrip() + f"\nREVIEW_DECISION: {decision}"
        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(resp)}")
        return resp


class SimpleVerify(Action):
    """Strong verification: syntax, tests presence, cross-reference, and stability digest."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "verifying artifacts")
        diagnostics: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False
        public_defs: Set[str] = set()

        # Code parsing and definitions
        try:
            code_tree = ast.parse(code or "")
            public_defs = {n.name for n in ast.walk(code_tree) if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_")}
            code_ok = True
            diagnostics.append(f"code_defs:{len(public_defs)}")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:140]}")

        # Tests parsing and presence of test/assert
        try:
            tests_tree = ast.parse(tests or "")
            has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(tests_tree))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(tests_tree)) or ("assert " in (tests or ""))
            tests_ok = bool(has_test_fn or has_assert)
            diagnostics.append("tests_presence:ok" if tests_ok else "tests_presence:none")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:140]}")

        # Cross-reference: tests mention at least one code symbol
        try:
            if public_defs and tests_ok:
                tests_names = {n.id for n in ast.walk(ast.parse(tests or "")) if isinstance(n, ast.Name)}
                referenced = bool(public_defs & tests_names)
                diagnostics.append("tests_reference:ok" if referenced else "tests_reference:none")
            else:
                diagnostics.append("tests_reference:skip")
        except Exception as e:
            diagnostics.append(f"reference_check_error:{str(e)[:120]}")

        verified = bool(code_ok and tests_ok and (referenced or not public_defs))
        # Stability digest
        try:
            digest = hashlib.sha256(((code or "") + "\n--TESTS--\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        except Exception:
            digest = "nodigest"

        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(diagnostics)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """Single-action role with explicit watch/triggers and idempotent processing."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    action: Optional[Action] = None
    watch_list: Set[str] = set()
    env: Optional["Environment"] = None
    _processed_local: Set[str] = set()

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.action = None
        self.watch_list = set()
        self.env = None
        self._processed_local = set()

    def set_action(self, action: Action):
        self.action = action

    def watch(self, actions: Iterable[Type[Action]]):
        self.watch_list = {a.name for a in actions}

    def should_handle(self, msg: Message) -> bool:
        if not msg:
            return False
        mid = getattr(msg, "id", None)
        if mid and mid in self._processed_local:
            return False
        if getattr(msg, "sent_from", None) == self.name:
            return False
        # explicit routing
        targets = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
        if targets:
            try:
                if isinstance(targets, (set, list)):
                    return bool(self.name in targets or self.profile in targets or "*" in targets)
                if isinstance(targets, str):
                    return targets in (self.name, self.profile, "*")
            except Exception:
                return False
        # triggers by cause_by
        if getattr(msg, "cause_by", "") in self.watch_list:
            return True
        # default: initial user input to coder
        if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in self.profile:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        tracer = getattr(self.context, "tracer", None)
        if not self.action:
            return None
        if message and not self.should_handle(message):
            # mark skipped as processed to avoid re-evaluation
            if getattr(message, "id", None):
                self._processed_local.add(message.id)
            return None

        try:
            if isinstance(self.action, SimpleWriteCode):
                idea = (getattr(message, "instruct_content", None) or getattr(message, "content", "")) if message else ""
                out = await self.action.run(idea)
                send_to = {"SimpleTester"}
            elif isinstance(self.action, SimpleWriteTest):
                # Use latest code artifact from env if none on message
                code_text = getattr(message, "content", "") if message and message.cause_by == SimpleWriteCode.name else ""
                if not code_text and getattr(self, "env", None):
                    latest = self.env.get_latest_by_cause(SimpleWriteCode.name)
                    code_text = latest.content if latest else ""
                out = await self.action.run(code_text)
                send_to = {"SimpleReviewer", "SimpleVerifier"}
            elif isinstance(self.action, SimpleWriteReview):
                code_msg = self.env.get_latest_by_cause(SimpleWriteCode.name) if self.env else None
                test_msg = self.env.get_latest_by_cause(SimpleWriteTest.name) if self.env else None
                out = await self.action.run(code_msg.content if code_msg else "", test_msg.content if test_msg else "")
                send_to = {"SimpleVerifier", "SimpleCoder"}  # share decision + feedback to coder
            elif isinstance(self.action, SimpleVerify):
                code_msg = self.env.get_latest_by_cause(SimpleWriteCode.name) if self.env else None
                test_msg = self.env.get_latest_by_cause(SimpleWriteTest.name) if self.env else None
                out = await self.action.run(code_msg.content if code_msg else "", test_msg.content if test_msg else "")
                send_to = set()  # broadcast semantics handled by orchestrator
            else:
                out = await self.action.run(getattr(message, "content", "") if message else "")
                send_to = set()
        except Exception as e:
            if tracer:
                tracer.log("ROLE_EXCEPTION", self.name, f"{type(e).__name__}: {str(e)[:160]}")
            out = f"ROLE_EXCEPTION: {type(e).__name__}: {str(e)}"
            send_to = {"SimpleCoder"}

        # mark input as processed locally
        if message and getattr(message, "id", None):
            self._processed_local.add(message.id)

        response = Message(
            content=out or "",
            role=self.profile,
            cause_by=self.action.name,
            sent_from=self.name,
            send_to=set(send_to)
        )
        # also mark produced id to avoid reprocessing own messages
        try:
            self._processed_local.add(response.id)
        except Exception:
            pass

        if tracer:
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={response.id} send_to={list(response.send_to)}")
        return response


