class Environment:
    """Message bus with per-role delivered tracking and artifact lookup."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # delivered: role_name -> set(message.id)
        self._delivered: Dict[str, Set[str]] = {}
        self._by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._delivered.setdefault(role.name, set())
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # Normalize routing set and ensure id
        try:
            if getattr(message, "send_to", None) is None:
                message.send_to = set(getattr(message, "sent_to", set()) or set())
        except Exception:
            message.send_to = set()
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        if getattr(message, "cause_by", None):
            self._by_cause.setdefault(message.cause_by, []).append(message)
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"Msg {message.id} from {message.sent_from} cause_by={message.cause_by} -> {list(message.send_to)} preview={preview}")

    def mark_delivered(self, role: Role, message: Message):
        mid = getattr(message, "id", None)
        if not mid:
            return
        self._delivered.setdefault(role.name, set()).add(mid)
        if self.tracer:
            self.tracer.log("ENV_MARK_DELIVERED", "Environment", f"{role.name} processed {mid}")

    def has_been_delivered(self, role: Role, message: Message) -> bool:
        mid = getattr(message, "id", None)
        return mid in self._delivered.get(role.name, set())

    def get_pending_for_role(self, role: Role) -> List[Message]:
        """Get messages for role: explicit routing or watch triggers. Skip duplicates and self-authored."""
        out: List[Message] = []
        delivered = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid or mid in delivered:
                continue
            if getattr(msg, "sent_from", None) == role.name:
                delivered.add(mid)
                continue
            # explicit routing
            targets = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
            if targets:
                try:
                    if isinstance(targets, (set, list)) and (role.name in targets or role.profile in targets or "*" in targets):
                        out.append(msg)
                        delivered.add(mid)
                        continue
                    if isinstance(targets, str) and targets in (role.name, role.profile, "*"):
                        out.append(msg)
                        delivered.add(mid)
                        continue
                except Exception:
                    pass
            # watch triggers
            if getattr(msg, "cause_by", None) in getattr(role, "watch_list", set()):
                out.append(msg)
                delivered.add(mid)
                continue
            # default: allow coder to see initial instruction
            if getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile:
                out.append(msg)
                delivered.add(mid)
                continue
        return out

    def get_latest_by_cause(self, cause: str) -> Optional[Message]:
        msgs = self._by_cause.get(cause, [])
        return msgs[-1] if msgs else None


class Team:
    """Deterministic orchestrator with stable verification-based termination and stall handling."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # stability tracking
        self._last_digest: Optional[str] = None
        self._pass_streak: int = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _invoke_role(self, role: Role, msg: Message) -> Optional[Message]:
        """Invoke role action with bounded retries to contain transient errors."""
        attempts = 0
        last_exc = None
        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                return await role.act(msg)
            except Exception as e:
                last_exc = e
                self.tracer.log("ROLE_EXCEPTION", role.name, f"Attempt {attempts} failed: {type(e).__name__}: {str(e)[:160]}")
                await asyncio.sleep(0.05 * attempts)
        return Message(
            content=f"ERROR: role {role.name} failed after {attempts} attempts: {type(last_exc).__name__}: {str(last_exc)[:160]}",
            role=role.profile,
            cause_by="RoleFailed",
            sent_from="Team",
            send_to=set()
        )

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds; need {REQUIRED_STABLE_PASSES} stable passes")
        # initial message routed to coder(s)
        initial_msg = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to={"SimpleCoder"}
        )
        self.env.publish_message(initial_msg)

        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        no_progress_rounds = 0
        last_history_len = len(self.env.history)
        rounds_ran = 0

        for r in range(1, n_round + 1):
            rounds_ran = r
            self.tracer.log("ROUND_START", "Team", f"Round {r}/{n_round}")
            produced = 0

            for RoleClass in order:
                for role in [ro for ro in self.env.roles if isinstance(ro, RoleClass)]:
                    pending = self.env.get_pending_for_role(role)
                    # force deliver initial to coder in first round
                    if isinstance(role, SimpleCoder) and r == 1 and initial_msg not in pending:
                        if not self.env.has_been_delivered(role, initial_msg):
                            pending = [initial_msg] + pending
                    for msg in pending:
                        resp = await self._invoke_role(role, msg)
                        # mark delivered regardless to avoid loops
                        self.env.mark_delivered(role, msg)
                        if resp:
                            self.env.publish_message(resp)
                            produced += 1
                            # verification streak tracking
                            if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                                content = resp.content or ""
                                if "VERIFICATION_RESULT: PASS" in content:
                                    # extract digest
                                    digest = None
                                    for part in content.split("|"):
                                        p = part.strip()
                                        if p.startswith("digest="):
                                            digest = p.split("=", 1)[1]
                                            break
                                    if digest and digest == self._last_digest:
                                        self._pass_streak += 1
                                    else:
                                        self._last_digest = digest
                                        self._pass_streak = 1
                                    self.tracer.log("VERIFY_PASS", "Team", f"digest={digest} streak={self._pass_streak}")
                                elif "VERIFICATION_RESULT: FAIL" in content:
                                    if self._pass_streak > 0:
                                        self.tracer.log("VERIFY_RESET", "Team", f"reset streak {self._pass_streak}->0")
                                    self._pass_streak = 0
                                    self._last_digest = None

            # Termination: require consecutive PASSes
            if self._pass_streak >= REQUIRED_STABLE_PASSES:
                self.tracer.log("TEAM_VERIFIED", "Team", f"Stable verification achieved (streak={self._pass_streak}); stopping")
                break

            # Stall detection and nudge
            if len(self.env.history) == last_history_len:
                no_progress_rounds += 1
            else:
                no_progress_rounds = 0
            last_history_len = len(self.env.history)

            if no_progress_rounds >= NO_PROGRESS_LIMIT:
                self.tracer.log("TEAM_NO_PROGRESS", "Team", f"No progress for {no_progress_rounds} rounds; terminating")
                break
            if produced == 0:
                # nudge coder(s)
                for coder in [ro for ro in self.env.roles if isinstance(ro, SimpleCoder)]:
                    nudge = Message(
                        content=f"Please refine or reattempt implementation for: {self.idea}",
                        instruct_content=self.idea,
                        role="System",
                        sent_from="Orchestrator",
                        cause_by="Nudge",
                        send_to={coder.profile}
                    )
                    self.env.publish_message(nudge)

            self.tracer.log("ROUND_END", "Team", f"Round {r} produced={produced} streak={self._pass_streak} history={len(self.env.history)}")
            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {rounds_ran} rounds with {len(self.env.history)} messages. verifier_streak={self._pass_streak}"
        self.tracer.log("SUMMARY", "Team", summary)

