import asyncio
import ast
import hashlib
import random
from typing import List, Dict, Optional, Set, Type, Iterable

# Simplified, robust multi-agent core aligned with MAST failure-mode reductions:
# - Clear single-responsibility roles with explicit routing and watch triggers
# - Robust LLM wrapper with retries/backoff and deterministic fallbacks
# - Deterministic orchestration with per-role delivery tracking
# - Strong verification with syntax/tests/reference checks and stable digest
# - Stable termination requiring consecutive PASSes with same digest and confirmation
# - Explicit error handling and retries for role execution


# Tunables
LLM_MAX_RETRIES = 3
LLM_BACKOFF_BASE = 0.5
ROLE_MAX_ATTEMPTS = 2
VERIFY_STABLE_REQUIRED = 2
NO_PROGRESS_GRACE = 2  # rounds without progress before nudging/stop
ROLE_ACT_TIMEOUT = 30.0

class Action(ABC):
    """Base action with LLM retry/backoff and explicit contract."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        try:
            if self.context and getattr(self.context, "config", None) and getattr(self.context.config, "llm", None):
                self.llm = LLMInterface(self.context.config.llm)
        except Exception:
            self.llm = None

    async def _ask_with_retry(self, messages: List[Dict[str, str]]) -> str:
        tracer = getattr(self.context, "tracer", None)
        last_err = None
        if not self.llm:
            if tracer:
                tracer.log("LLM_FALLBACK", self.name, "LLM unavailable; returning deterministic fallback")
            return "LLM_UNAVAILABLE"
        for attempt in range(1, LLM_MAX_RETRIES + 1):
            try:
                if tracer:
                    tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{LLM_MAX_RETRIES}")
                resp = await self.llm.ask(messages)
                if not isinstance(resp, str) or not resp.strip():
                    last_err = "empty_response"
                    raise RuntimeError("Empty LLM response")
                low = resp.strip().lower()
                if low.startswith("error") or "error communicating" in low:
                    last_err = resp
                    raise RuntimeError("LLM error content")
                if tracer:
                    tracer.log("LLM_OK", self.name, f"len={len(resp)}")
                return resp
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {last_err[:200]}")
                if attempt < LLM_MAX_RETRIES:
                    backoff = LLM_BACKOFF_BASE * (2 ** (attempt - 1))
                    jitter = random.uniform(0, backoff * 0.2)
                    await asyncio.sleep(backoff + jitter)
        if tracer:
            tracer.log("LLM_GIVEUP", self.name, f"Exhausted retries: {last_err}")
        return f"LLM_FAILED:{last_err or 'unknown'}"

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        pass


class SimpleWriteCode(Action):
    """Produce an implementation from an idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"idea_len={len(idea or '')}")
        if not idea or not idea.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Empty idea -> fallback")
            return (
                "def placeholder(value=None):\n"
                "    \"\"\"Deterministic fallback when no idea provided.\"\"\"\n"
                "    return value\n"
            )
        prompt = (
            "You are an expert Python developer. Return only valid Python source code implementing the task. "
            "Include docstrings and simple input validation.\n\n"
            f"Task:\n{idea}"
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt},
        ]
        code = await self._ask_with_retry(messages)
        # Validate code parses; fallback deterministically otherwise
        try:
            ast.parse(code or "")
        except Exception as e:
            if tracer:
                tracer.log("ACTION_VALIDATE_FAIL", self.name, f"Code parse error: {str(e)[:160]}")
            return (
                f'"""Fallback for task: {idea[:120]}"""\n'
                "def placeholder(value=None):\n"
                "    \"\"\"Fallback due to invalid generated code.\"\"\"\n"
                "    return value\n"
            )
        if tracer:
            tracer.log("ACTION_END", self.name, f"code_len={len(code or '')}")
        return code


class SimpleWriteTest(Action):
    """Generate pytest tests for given code."""
    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"code_len={len(code or '')}")
        if not code or not code.strip():
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "No code provided -> minimal test")
            return "def test_placeholder():\n    assert True\n"
        # public symbols for reference
        symbols: List[str] = []
        try:
            tree = ast.parse(code or "")
            for n in tree.body:
                if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_"):
                    symbols.append(n.name)
        except Exception:
            pass
        prompt = (
            "You are a QA engineer. Write pytest-style tests for the provided Python module. "
            "Include edge cases and use assert statements. Return only test code.\n\n"
            f"Public symbols: {', '.join(symbols[:8]) or '(none)'}\n\n"
            f"Module (truncated):\n{(code or '')[:3000]}"
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt},
        ]
        tests = await self._ask_with_retry(messages)

        # Validate tests (parse + has assert or test_ functions); deterministic fallback references a symbol
        def fallback(sym: Optional[str]) -> str:
            target = sym or "placeholder"
            return (
                f"def test_{target}_basic_reference_and_truth():\n"
                f"    {target}\n"
                "    assert True\n"
            )

        try:
            parsed = ast.parse(tests or "")
            has_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed)) or ("assert " in (tests or ""))
            if not (has_fn or has_assert):
                raise ValueError("No tests/asserts found")
        except Exception:
            tests = fallback(symbols[0] if symbols else None)
            if tracer:
                tracer.log("ACTION_FALLBACK", self.name, "Tests invalid -> fallback")
        if tracer:
            tracer.log("ACTION_END", self.name, f"tests_len={len(tests or '')}")
        return tests


class SimpleWriteReview(Action):
    """Review code and tests, emit decision token."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, f"human={self.is_human}")
        issues = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error:{str(e)[:120]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error:{str(e)[:120]}")

        if self.is_human:
            decision = "REQUEST_CHANGES" if issues else "APPROVE"
            review = "HUMAN_REVIEW: " + ("; ".join(issues) if issues else "ok") + f"\nREVIEW_DECISION: {decision}"
            if tracer:
                tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
            return review

        prompt = (
            "You are a senior reviewer. Provide a concise, actionable review and end with:\n"
            "REVIEW_DECISION: APPROVE or REVIEW_DECISION: REQUEST_CHANGES\n\n"
            f"STATIC_ISSUES: {issues or 'none'}\n\n"
            f"Code (truncated):\n{(code or '')[:1800]}\n\n"
            f"Tests (truncated):\n{(tests or '')[:1800]}"
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code reviews."},
            {"role": "user", "content": prompt},
        ]
        review = await self._ask_with_retry(messages)
        if not isinstance(review, str) or "REVIEW_DECISION:" not in review:
            decision = "APPROVE" if not issues else "REQUEST_CHANGES"
            review = (review or "Automated review unavailable.").strip() + f"\nREVIEW_DECISION: {decision}"
        if tracer:
            tracer.log("ACTION_END", self.name, f"review_len={len(review)}")
        return review


class SimpleVerify(Action):
    """Deterministic verification: syntax + tests + cross-reference + digest."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        tracer = getattr(self.context, "tracer", None)
        if tracer:
            tracer.log("ACTION_START", self.name, "Verifying artifacts")
        details: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False

        # Code syntax and defs
        public_defs: Set[str] = set()
        try:
            code_tree = ast.parse(code or "")
            public_defs = {n.name for n in ast.walk(code_tree) if isinstance(n, (ast.FunctionDef, ast.ClassDef)) and not n.name.startswith("_")}
            code_ok = True
            details.append(f"code_defs:{len(public_defs)}")
        except Exception as e:
            details.append(f"code_parse_error:{str(e)[:160]}")

        # Tests syntax and presence (asserts or test_ functions)
        try:
            tests_tree = ast.parse(tests or "")
            has_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(tests_tree))
            has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(tests_tree)) or ("assert " in (tests or ""))
            tests_ok = bool(has_fn or has_assert)
            details.append("tests_presence:ok" if tests_ok else "tests_presence:none")
        except Exception as e:
            details.append(f"tests_parse_error:{str(e)[:160]}")
            tests_tree = None

        # Cross-reference
        if public_defs and tests_ok:
            tests_text = tests or ""
            referenced = any(sym in tests_text for sym in public_defs)
            details.append("tests_reference:ok" if referenced else "tests_reference:none")
        else:
            details.append("tests_reference:skip")

        verified = bool(code_ok and tests_ok and (referenced or not public_defs))
        # Stable digest
        try:
            digest = hashlib.sha256(((code or "") + "\n--TESTS--\n" + (tests or "")).encode("utf-8")).hexdigest()[:12]
        except Exception:
            digest = "nodigest"
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | digest={digest} | " + "; ".join(details)
        if tracer:
            tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """Single-responsibility role with explicit routing and watch triggers."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    env: Optional["Environment"] = None
    actions: List[Action] = []
    watch_list: Set[str] = set()
    is_human: bool = False

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.env = kwargs.get("env", None)
        self.actions = []
        self.watch_list = set()
        self._processed_ids: Set[str] = set()

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def watch_actions(self, actions: Iterable[Type[Action]]):
        self.watch_list = {a.name for a in actions}

    def should_handle(self, msg: Message) -> bool:
        if msg is None:
            return False
        if getattr(msg, "sent_from", None) == self.name:
            return False
        mid = getattr(msg, "id", None)
        if mid and mid in self._processed_ids:
            return False
        targets = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None)
        if targets:
            try:
                if isinstance(targets, (set, list)):
                    if self.name in targets or self.profile in targets or "*" in targets:
                        return True
                    return False
                if isinstance(targets, str):
                    return targets in (self.name, self.profile, "*")
            except Exception:
                return False
        return getattr(msg, "cause_by", "") in self.watch_list or (
            getattr(msg, "cause_by", "") == "UserInput" and "Coder" in self.profile
        )

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        tracer = getattr(self.context, "tracer", None)
        if not self.actions:
            if tracer:
                tracer.log("ROLE_NO_ACTION", self.name, "No actions configured")
            return None
        action = self.actions[0]
        if message is not None and not self.should_handle(message):
            if tracer:
                tracer.log("ROLE_SKIP", self.name, f"Skip msg={getattr(message,'id',None)}")
            return None

        attempts = 0
        last_err = None
        while attempts < ROLE_MAX_ATTEMPTS:
            attempts += 1
            try:
                if isinstance(action, SimpleWriteCode):
                    idea = (getattr(message, "instruct_content", None) or getattr(message, "content", "")) if message else ""
                    out = await action.run(idea)
                    recipients = {"SimpleTester"}
                elif isinstance(action, SimpleWriteTest):
                    code_text = ""
                    if message and getattr(message, "cause_by", "") == SimpleWriteCode.name:
                        code_text = getattr(message, "content", "") or ""
                    if not code_text and self.env:
                        latest = self.env.get_latest_by_cause(SimpleWriteCode.name)
                        code_text = latest.content if latest else ""
                    out = await action.run(code_text)
                    recipients = {"SimpleReviewer", "SimpleVerifier"}
                elif isinstance(action, SimpleWriteReview):
                    code_msg = self.env.get_latest_by_cause(SimpleWriteCode.name) if self.env else None
                    tests_msg = self.env.get_latest_by_cause(SimpleWriteTest.name) if self.env else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    lowered = (out or "").lower()
                    recipients = {"SimpleVerifier"} if "approve" in lowered else {"SimpleCoder", "SimpleTester"}
                elif isinstance(action, SimpleVerify):
                    code_msg = self.env.get_latest_by_cause(SimpleWriteCode.name) if self.env else None
                    tests_msg = self.env.get_latest_by_cause(SimpleWriteTest.name) if self.env else None
                    out = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                    recipients = set()  # broadcast semantics handled by orchestrator
                else:
                    out = await action.run(getattr(message, "content", "") if message else "")
                    recipients = set()

                # mark processed to avoid duplicates
                if message and getattr(message, "id", None):
                    self._processed_ids.add(message.id)

                response = Message(
                    content=out,
                    role=self.profile,
                    cause_by=action.name,
                    sent_from=self.name,
                    send_to=recipients
                )
                if tracer:
                    tracer.log("ROLE_COMPLETE", self.name, f"Produced msg={getattr(response,'id',None)} to={recipients}")
                return response
            except Exception as e:
                last_err = str(e)
                if tracer:
                    tracer.log("ROLE_ERROR", self.name, f"Attempt {attempts} failed: {last_err[:200]}")
                await asyncio.sleep(0.05 * attempts)

        fail_msg = f"ROLE_FAILED: {self.name} after {ROLE_MAX_ATTEMPTS} attempts - {last_err}"
        if tracer:
            tracer.log("ROLE_FAIL", self.name, fail_msg[:200])
        return Message(
            content=fail_msg,
            role=self.profile,
            cause_by="RoleFailure",
            sent_from=self.name,
            send_to={"Team"}
        )


