class Environment:
    """Message bus with per-role delivery tracking and artifact lookup."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # delivered map: role.name -> set(msg_id)
        self._delivered: Dict[str, Set[str]] = {}
        # index by cause_by
        self._by_cause: Dict[str, List[Message]] = {}

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        self._delivered.setdefault(role.name, set())
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # normalize send_to
        if getattr(message, "send_to", None) is None:
            try:
                message.send_to = set(getattr(message, "sent_to", set()) or set())
            except Exception:
                message.send_to = set()
        # ensure id
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        cb = getattr(message, "cause_by", "")
        if cb:
            self._by_cause.setdefault(cb, []).append(message)
        if self.tracer:
            preview = (message.content or "")[:160].replace("\n", " ")
            self.tracer.log("ENV_MESSAGE", "Environment", f"Msg {message.id} from {message.sent_from} cause_by={cb} -> {list(message.send_to)} preview={preview}")

    def get_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages not yet delivered to this role that it should handle; mark as delivered."""
        out: List[Message] = []
        delivered = self._delivered.setdefault(role.name, set())
        for msg in self.history:
            mid = getattr(msg, "id", None)
            if not mid or mid in delivered:
                continue
            if getattr(msg, "sent_from", None) == role.name:
                delivered.add(mid)
                continue
            # explicit routing
            send_to = getattr(msg, "send_to", None) or getattr(msg, "sent_to", None) or set()
            targeted = False
            try:
                if isinstance(send_to, (set, list)):
                    targeted = bool(role.name in send_to or role.profile in send_to or "*" in send_to)
                elif isinstance(send_to, str):
                    targeted = send_to in (role.name, role.profile, "*")
            except Exception:
                targeted = False
            if targeted or (getattr(msg, "cause_by", "") in role.watch_list) or (
                getattr(msg, "cause_by", "") == "UserInput" and "Coder" in role.profile
            ):
                out.append(msg)
                delivered.add(mid)
        return out

    def get_latest_by_cause(self, cause_name: str) -> Optional[Message]:
        msgs = self._by_cause.get(cause_name, [])
        return msgs[-1] if msgs else None


class Team:
    """Deterministic orchestrator with verification stability and robust retries."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.idea: str = ""
        self._required_stable: int = VERIFY_STABLE_REQUIRED
        self._streak: int = 0
        self._last_digest: Optional[str] = None

    def hire(self, roles: List[Role]):
        for r in roles:
            r.context = self.context
            r.env = self.env
            self.env.add_role(r)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _confirm_verification(self) -> bool:
        """Local deterministic confirmation to reduce premature termination."""
        # Gather latest artifacts
        code_msg = self.env.get_latest_by_cause(SimpleWriteCode.name)
        tests_msg = self.env.get_latest_by_cause(SimpleWriteTest.name)
        verifier = SimpleVerify(context=self.context)
        result = await verifier.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
        return isinstance(result, str) and "VERIFICATION_RESULT: PASS" in result

    async def _process_role(self, role: Role) -> int:
        """Process messages for a role; return number of new messages produced."""
        produced = 0
        msgs = self.env.get_messages_for_role(role)
        for m in msgs:
            try:
                resp = await asyncio.wait_for(role.act(m), timeout=ROLE_ACT_TIMEOUT)
            except asyncio.TimeoutError:
                self.tracer.log("ROLE_TIMEOUT", role.name, f"Timeout processing msg {getattr(m,'id',None)}")
                resp = Message(content=f"ROLE_TIMEOUT: {role.name}", role=role.profile, cause_by="RoleTimeout", sent_from=role.name, send_to=set())
            except Exception as e:
                self.tracer.log("ROLE_EXCEPTION", role.name, f"{type(e).__name__}: {str(e)[:200]}")
                resp = Message(content=f"ROLE_EXCEPTION: {type(e).__name__}:{str(e)[:200]}", role=role.profile, cause_by="RoleException", sent_from=role.name, send_to=set())

            if resp:
                self.env.publish_message(resp)
                produced += 1
                # verification tracking
                if isinstance(role, SimpleVerifier) and isinstance(resp.content, str):
                    content = resp.content
                    if "VERIFICATION_RESULT: PASS" in content:
                        digest = None
                        for part in content.split("|"):
                            p = part.strip()
                            if p.startswith("digest="):
                                digest = p.split("=", 1)[1]
                                break
                        if digest and digest == self._last_digest:
                            self._streak += 1
                        else:
                            self._last_digest = digest
                            self._streak = 1
                        self.tracer.log("VERIFY_UPDATE", "Team", f"digest={digest} streak={self._streak}")
                    elif "VERIFICATION_RESULT: FAIL" in content:
                        if self._streak:
                            self.tracer.log("VERIFY_RESET", "Team", f"reset {self._streak}->0")
                        self._streak = 0
                        self._last_digest = None
        return produced

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds; require {self._required_stable} stable passes")
        # kickoff message to coder(s)
        coder_targets = {r.profile for r in self.env.roles if isinstance(r, SimpleCoder)} or {"SimpleCoder"}
        initial = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to=coder_targets
        )
        self.env.publish_message(initial)

        order = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        last_history_len = len(self.env.history)
        no_progress_rounds = 0

        for round_idx in range(1, n_round + 1):
            self.tracer.log("ROUND_START", "Team", f"Round {round_idx}/{n_round}")
            produced_total = 0

            for role_cls in order:
                for role in [r for r in self.env.roles if isinstance(r, role_cls)]:
                    produced_total += await self._process_role(role)

            # progress and termination checks
            progress = len(self.env.history) > last_history_len
            last_history_len = len(self.env.history)
            no_progress_rounds = 0 if progress else (no_progress_rounds + 1)
            self.tracer.log("ROUND_END", "Team", f"produced={produced_total} streak={self._streak} history={last_history_len}")

            if self._streak >= self._required_stable:
                # confirm pass before stopping
                confirmed = await self._confirm_verification()
                if confirmed:
                    self.tracer.log("TEAM_EARLY_STOP", "Team", f"Stable verification confirmed (streak={self._streak}); stopping")
                    break
                else:
                    self.tracer.log("VERIFY_CONFIRM_FAIL", "Team", "Confirmation failed; resetting streak")
                    self._streak = 0
                    self._last_digest = None

            if not progress:
                if no_progress_rounds == 1:
                    # nudge coder(s)
                    self.tracer.log("TEAM_NUDGE", "Team", "No progress; nudging coder(s)")
                    for coder in [r for r in self.env.roles if isinstance(r, SimpleCoder)]:
                        nudge = Message(
                            content=f"Nudge: please refine implementation for '{self.idea}'.",
                            instruct_content=self.idea,
                            role="System",
                            sent_from="Orchestrator",
                            cause_by="Nudge",
                            send_to={coder.profile}
                        )
                        self.env.publish_message(nudge)
                elif no_progress_rounds >= NO_PROGRESS_GRACE:
                    self.tracer.log("TEAM_NO_PROGRESS", "Team", f"Stalled for {no_progress_rounds} rounds; terminating")
                    break

            await asyncio.sleep(0.01)

        self.tracer.log("TEAM_END", "Team", "Project run finished")
        summary = f"Project '{self.idea}' finished after {round_idx} rounds with {len(self.env.history)} messages. Verified={(self._streak >= self._required_stable)}"
        self.tracer.log("SUMMARY", "Team", summary)

