class Action(ABC):
    """Base action with clear responsibilities, LLM retry logic, and graceful failure reporting."""
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_retries: int = 3
    retry_backoff: float = 1.0  # seconds

    def __init__(self, **kwargs):
        self.context = kwargs.get("context")
        if self.context and getattr(self.context, "config", None) and self.context.config.llm:
            self.llm = LLMInterface(self.context.config.llm)

    async def _call_llm_with_retries(self, messages: List[Dict[str, str]]) -> str:
        """Call the LLM with retries, logging, and backoff. Returns a string (or structured error)."""
        last_err = None
        for attempt in range(1, self.max_retries + 1):
            try:
                if not self.llm:
                    # Deterministic fallback to avoid raising
                    return "LLM_UNAVAILABLE_FALLBACK"
                resp = await self.llm.ask(messages)
                # LLMInterface returns strings; treat strings starting with "Error" as failures
                if isinstance(resp, str) and resp.startswith("Error"):
                    raise RuntimeError(resp)
                return resp
            except Exception as e:
                last_err = e
                if self.context and self.context.tracer:
                    self.context.tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {str(e)}")
                # Exponential backoff
                await asyncio.sleep(self.retry_backoff * attempt)
        # After retries, return structured error so downstream can handle without exception
        err_msg = f"ERROR: LLM failed after {self.max_retries} attempts: {str(last_err)}"
        if self.context and self.context.tracer:
            self.context.tracer.log("LLM_FAIL", self.name, err_msg)
        return err_msg

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError


class SimpleWriteCode(Action):
    """Produce a code artifact from an idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, f"Generating code for idea: {idea[:120]}")
        prompt = (
            "You are a professional Python developer. Produce a single Python module that implements the described functionality.\n"
            "Requirements:\n"
            "- Clean, functional Python code\n"
            "- Proper error handling\n"
            "- Docstrings/comments\n"
            "- Parsable by ast.parse\n\n"
            f"Task: {idea}\n\n"
            "Return only the Python code, no wrappers or explanation."
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt}
        ]
        result = await self._call_llm_with_retries(messages)
        # Quick static validation: try parse; if LLM returned an error string starting with "ERROR:", fallback to minimal code
        parsed_ok = False
        if isinstance(result, str) and not result.startswith("ERROR:") and result.strip():
            try:
                ast.parse(result)
                parsed_ok = True
            except Exception as e:
                if self.context and self.context.tracer:
                    self.context.tracer.log("ACTION_VALIDATION", self.name, f"AST parse failed: {str(e)}")
        if not parsed_ok:
            # Fallback minimal safe module
            fallback = (
                f"# Fallback implementation for idea: {idea}\n"
                "def placeholder():\n"
                "    \"\"\"Fallback placeholder function.\"\"\"\n"
                "    return None\n"
            )
            if self.context and self.context.tracer:
                self.context.tracer.log("ACTION_FALLBACK", self.name, "Using fallback implementation")
            return fallback
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, f"Generated code length={len(result)}")
        return result


