class SimpleVerify(Action):
    """Deterministic verification combining static checks and heuristics."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, "Performing verification checks")
        diagnostics = []
        code_ok = False
        tests_ok = False

        # Code parse and basic heuristics
        try:
            code_ast = ast.parse(code or "")
            defs = [n for n in code_ast.body if isinstance(n, (ast.FunctionDef, ast.ClassDef))]
            if defs:
                code_ok = True
                diagnostics.append(f"code_defs:{len(defs)}")
            else:
                diagnostics.append("code_defs:0")
        except Exception as e:
            diagnostics.append(f"code_parse_error:{str(e)[:160]}")

        # Tests parse and heuristics
        try:
            tests_ast = ast.parse(tests or "")
            tests_ok = bool(tests and tests.strip())
            if tests_ok and code_ok:
                # Check whether tests reference code functions
                code_names = {n.name for n in ast.walk(code_ast) if isinstance(n, ast.FunctionDef)}
                test_names = {n.id for n in ast.walk(tests_ast) if isinstance(n, ast.Name)}
                referenced = code_names & test_names
                diagnostics.append(f"tests_refs:{len(referenced)}")
                if not referenced:
                    diagnostics.append("tests_refs:0_match")
            else:
                diagnostics.append("tests_parse_or_empty")
        except Exception as e:
            diagnostics.append(f"tests_parse_error:{str(e)[:160]}")

        verified = code_ok and tests_ok and not any("parse_error" in d for d in diagnostics)
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | " + "; ".join(diagnostics)
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, result)
        return result


class Role(ABC):
    """Base role with explicit responsibilities, watch/trigger logic, failure tracking, and clear outputs."""
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: Set[str] = set()  # names of causes to listen for
    responsibility: str = "Undefined"
    max_failures_before_escalation: int = 2

    def __init__(self, **kwargs):
        self.name = kwargs.get("name", self.name)
        self.profile = kwargs.get("profile", self.profile)
        self.context = kwargs.get("context")
        self.is_human = kwargs.get("is_human", False)
        self.actions = []
        self.watch_list = set()
        self.responsibility = kwargs.get("responsibility", self.responsibility)
        self.failure_count = 0
        # env will be assigned by Team.hire
        self.env: Optional[Environment] = None

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def _watch(self, actions: List[str]):
        """Provide action names or cause strings that this role responds to."""
        self.watch_list = set(actions)

    def should_act_on(self, message: Optional[Message]) -> bool:
        """Decide whether to act on a given message."""
        if message is None:
            return False
        # Direct addressing
        send_to = getattr(message, "send_to", None)
        if send_to and (self.profile in send_to or self.name in send_to):
            return True
        # Cause-based matching
        if getattr(message, "cause_by", None) in self.watch_list:
            return True
        return False

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """Execute primary action for the role with error handling and failure accounting."""
        if not self.actions:
            return None
        action = self.actions[0]
        if self.context and self.context.tracer:
            self.context.tracer.log("ROLE_ACT", self.name, f"Invoking {action.name} for message_id={(getattr(message,'id',None))}")
        try:
            # Map inputs per action type
            if isinstance(action, SimpleWriteCode):
                idea = getattr(message, "instruct_content", None) or (message.content if message else "")
                result = await action.run(idea or "")
            elif isinstance(action, SimpleWriteTest):
                code_src = getattr(message, "content", "") if message else ""
                result = await action.run(code_src)
            elif isinstance(action, SimpleWriteReview):
                # Attempt to collect latest code & tests
                code_msg, tests_msg = self.env.find_latest_pair(SimpleWriteCode.name, SimpleWriteTest.name) if self.env else (None, None)
                result = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
            elif isinstance(action, SimpleVerify):
                code_msg, tests_msg = self.env.find_latest_pair(SimpleWriteCode.name, SimpleWriteTest.name) if self.env else (None, None)
                result = await action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
            else:
                # Generic
                result = await action.run(getattr(message, "content", "") if message else "")
        except Exception as e:
            if self.context and self.context.tracer:
                self.context.tracer.log("ROLE_ERROR", self.name, f"Unhandled exception: {str(e)}")
            result = f"ERROR: Role {self.name} exception: {str(e)}"

        # Account failure vs success
        if isinstance(result, str) and result.startswith("ERROR:"):
            self.failure_count += 1
        else:
            self.failure_count = 0

        # Build message
        out_msg = Message(
            content=result,
            role=self.profile,
            cause_by=action.name,
            sent_from=self.name,
            send_to=set()
        )

        if self.context and self.context.tracer:
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Produced message {getattr(out_msg,'id',None)} cause_by={out_msg.cause_by}")

        # Escalate if repeated failures (log only; escalation policy can be extended)
        if self.failure_count > self.max_failures_before_escalation and self.context and self.context.tracer:
            self.context.tracer.log("ESCALATION", self.name, f"Failure count {self.failure_count} exceeded threshold")

        return out_msg


