            try:
                if not self.llm:
                    # Deterministic fallback to avoid raising
                    return "LLM_UNAVAILABLE_FALLBACK"
                resp = await self.llm.ask(messages)
                # LLMInterface returns strings; treat strings starting with "Error" as failures
                if isinstance(resp, str) and resp.startswith("Error"):
                    raise RuntimeError(resp)
                return resp
            except Exception as e:
                last_err = e
                if self.context and self.context.tracer:
                    self.context.tracer.log("LLM_RETRY", self.name, f"Attempt {attempt} failed: {str(e)}")
                # Exponential backoff
                await asyncio.sleep(self.retry_backoff * attempt)
        # After retries, return structured error so downstream can handle without exception
        err_msg = f"ERROR: LLM failed after {self.max_retries} attempts: {str(last_err)}"
        if self.context and self.context.tracer:
            self.context.tracer.log("LLM_FAIL", self.name, err_msg)
        return err_msg

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        raise NotImplementedError


