class Environment:
    """Environment manages messages, processing state, and routing logic."""
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        # track which roles processed which message ids
        self._processed: Dict[str, Set[str]] = {}
        # attempts to avoid livelock
        self._attempts: Dict[str, int] = {}

    def add_role(self, role: Role):
        self.roles.append(role)
        role.env = self
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # ensure message has id
        if not getattr(message, "id", None):
            try:
                message.id = str(uuid.uuid4())
            except Exception:
                pass
        self.history.append(message)
        self._processed.setdefault(message.id, set())
        self._attempts.setdefault(message.id, 0)
        if self.tracer:
            self.tracer.log("ENV_MESSAGE", "Environment", f"Message {getattr(message,'id','')} from {message.sent_from}: {str(message.content)[:150]} | cause_by={message.cause_by}")

    def mark_processed(self, message: Message, role: Role):
        self._processed.setdefault(message.id, set()).add(role.name)
        if self.tracer:
            self.tracer.log("ENV_PROCESSED", "Environment", f"Message {message.id} processed by {role.name}")

    def increment_attempt(self, message: Message) -> int:
        self._attempts[message.id] = self._attempts.get(message.id, 0) + 1
        return self._attempts[message.id]

    def get_unprocessed_messages_for_role(self, role: Role) -> List[Message]:
        """Return messages matching role.watch_list or direct addressing that haven't been processed by role."""
        results: List[Message] = []
        for msg in self.history:
            # skip messages already processed by this role
            processed = self._processed.get(msg.id, set())
            if role.name in processed:
                continue
            # direct addressing
            send_to = getattr(msg, "send_to", None)
            if send_to and (role.profile in send_to or role.name in send_to):
                results.append(msg)
                continue
            # cause matching
            if getattr(msg, "cause_by", None) in role.watch_list:
                results.append(msg)
        return results

    def find_latest_pair(self, code_cause: str, tests_cause: str):
        """Find the latest code and test messages (best-effort)."""
        code_msg = None
        tests_msg = None
        for msg in reversed(self.history):
            if not code_msg and getattr(msg, "cause_by", None) == code_cause:
                code_msg = msg
            if not tests_msg and getattr(msg, "cause_by", None) == tests_cause:
                tests_msg = msg
            if code_msg and tests_msg:
                break
        return code_msg, tests_msg


class Team:
    """Orchestrates the multi-agent pipeline with explicit termination, verification confirmation, and stagnation detection."""
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # pipeline order enforces clear responsibilities
        self.pipeline = [SimpleCoder, SimpleTester, SimpleReviewer, SimpleVerifier]
        self.max_rounds_without_progress = 3

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def run(self, n_round: int = 4):
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds")
        # initial user message broadcast to all roles
        initial_msg = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to=set([r.profile for r in self.env.roles]) if self.env.roles else set()
        )
        # publish initial message (roles will be added after hire in the usual flow; ensure send_to can be empty safely)
        self.env.publish_message(initial_msg)

        verified = False
        rounds_since_progress = 0
        last_history_len = len(self.env.history)

        for round_num in range(1, n_round + 1):
            self.tracer.log("ROUND_START", "Team", f"Round {round_num}/{n_round}")
            progress_this_round = False

            # For each pipeline stage, process roles deterministically
            for role_cls in self.pipeline:
                for role in [r for r in self.env.roles if isinstance(r, role_cls)]:
                    # Gather unprocessed messages for this role (or allow coder to act on initial)
                    inbox = self.env.get_unprocessed_messages_for_role(role)
                    # Special-case coder to allow acting on user input in first round even if not in watch_list
                    if isinstance(role, SimpleCoder) and round_num == 1:
                        # ensure initial message is considered
                        for msg in reversed(self.env.history):
                            if getattr(msg, "cause_by", "") == "UserInput":
                                if msg not in inbox:
                                    inbox.insert(0, msg)
                                break

                    # Process messages one at a time to remain deterministic
                    for msg in list(inbox):
                        # Avoid repeated attempts causing livelock
                        attempts = self.env.increment_attempt(msg)
                        if attempts > 6:
                            # mark processed to move on
                            self.env.mark_processed(msg, role)
                            if self.tracer:
                                self.tracer.log("ENV_SKIP", "Team", f"Skipping message {msg.id} for {role.name} after {attempts} attempts")
                            continue

                        # Guard: do not let a role process its own previous message
                        if getattr(msg, "sent_from", None) == role.name:
                            self.env.mark_processed(msg, role)
                            continue

                        try:
                            response = await role.act(msg)
                        except Exception as e:
                            # Unexpected error: produce an error message and continue
                            self.tracer.log("ROLE_EXCEPTION", role.name, f"Exception during act: {str(e)}")
                            response = Message(
                                content=f"ERROR: Unexpected exception in role {role.name}: {str(e)}",
                                role=role.profile,
                                cause_by="EXCEPTION",
                                sent_from=role.name,
                                send_to=set()
                            )

                        if response:
                            # Route response to appropriate next roles
                            if isinstance(role, SimpleCoder):
                                response.send_to = set([SimpleTester.profile])
                            elif isinstance(role, SimpleTester):
                                response.send_to = set([SimpleReviewer.profile, SimpleVerifier.profile])
                            elif isinstance(role, SimpleReviewer):
                                # If reviewer requested change, send to coder, otherwise to verifier
                                content_upper = (response.content or "").strip().upper()
                                if content_upper.startswith("REQUEST_CHANGE"):
                                    response.send_to = set([SimpleCoder.profile])
                                    response.cause_by = "REQUEST_CHANGE"
                                else:
                                    response.send_to = set([SimpleVerifier.profile])
                            elif isinstance(role, SimpleVerifier):
                                # Broadcast verdict
                                response.send_to = set([r.profile for r in self.env.roles])

                            self.env.publish_message(response)
                            self.env.mark_processed(msg, role)
                            progress_this_round = True

                            # If verifier produced a PASS, confirm deterministically
                            if isinstance(role, SimpleVerifier) and isinstance(response.content, str) and "VERIFICATION_RESULT: PASS" in response.content:
                                # confirm using verifier action on latest artifacts
                                code_msg, tests_msg = self.env.find_latest_pair(SimpleWriteCode.name, SimpleWriteTest.name)
                                verifier_action = role.actions[0] if role.actions else None
                                confirmed = False
                                if verifier_action:
                                    try:
                                        confirm_text = await verifier_action.run(code_msg.content if code_msg else "", tests_msg.content if tests_msg else "")
                                        if isinstance(confirm_text, str) and "VERIFICATION_RESULT: PASS" in confirm_text:
                                            confirmed = True
                                    except Exception as e:
                                        if self.tracer:
                                            self.tracer.log("VERIFY_CONFIRM_ERROR", "Team", f"Confirmation attempt error: {str(e)}")
                                if confirmed:
                                    verified = True
                                    if self.tracer:
                                        self.tracer.log("VERIFIED_CONFIRMED", "Team", f"Verification confirmed in round {round_num}")
                                else:
                                    if self.tracer:
                                        self.tracer.log("VERIFIED_REJECTED", "Team", f"Verification not confirmed in round {round_num}")

            # Round end bookkeeping
            if len(self.env.history) == last_history_len:
                rounds_since_progress += 1
            else:
                rounds_since_progress = 0
            last_history_len = len(self.env.history)

            self.tracer.log("ROUND_END", "Team", f"Round {round_num} completed; progress={progress_this_round}; history_len={len(self.env.history)}")

            # Termination conditions
            if verified:
                self.tracer.log("TEAM_EARLY_STOP", "Team", f"Verified; stopping in round {round_num}")
                break
            if rounds_since_progress >= self.max_rounds_without_progress:
                self.tracer.log("TEAM_HANG_DETECT", "Team", f"No progress for {rounds_since_progress} rounds; stopping to avoid livelock")
                break

        self.tracer.log("TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {round_num} rounds with {len(self.env.history)} messages exchanged. Verified={verified}"
        self.tracer.log("SUMMARY", "Team", summary)

