import asyncio
import time
import ast
from typing import Tuple

class Action(ABC):
    """
    Base Action with:
    - clear responsibility: implement run(...)
    - robust LLM call wrapper with retries and exponential backoff
    - access to context and tracer
    """
    name: str = "Action"
    context: Optional[Context] = None
    llm: Optional[LLMInterface] = None
    max_llm_retries: int = 3
    retry_backoff: float = 1.0  # base seconds

    def __init__(self, **kwargs):
        self.context = kwargs.get('context')
        if self.context and getattr(self.context, "config", None) and self.context.config.llm:
            try:
                self.llm = LLMInterface(self.context.config.llm)
            except Exception:
                self.llm = None

    async def _ask_with_retry(self, messages: List[Dict[str, str]]) -> str:
        """
        Calls the LLM with exponential backoff retry strategy.
        Returns a string response or a clear error-marked string on final failure.
        """
        last_err = None
        for attempt in range(1, self.max_llm_retries + 1):
            try:
                if self.context and self.context.tracer:
                    self.context.tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_llm_retries}")
                if not self.llm:
                    # Deterministic fallback to avoid API dependency in tests
                    return "LLM_UNAVAILABLE: fallback response"
                resp = await self.llm.ask(messages)
                # Basic error detection for our LLMInterface responses
                if isinstance(resp, str) and (resp.strip().lower().startswith("error") or "error communicating" in resp.lower()):
                    last_err = resp
                    if self.context and self.context.tracer:
                        self.context.tracer.log("LLM_RETRY", self.name, f"LLM returned error: {resp[:200]}")
                    await asyncio.sleep(self.retry_backoff * (2 ** (attempt - 1)))
                    continue
                # Success
                return resp
            except Exception as e:
                last_err = str(e)
                if self.context and self.context.tracer:
                    self.context.tracer.log("LLM_EXCEPTION", self.name, f"Attempt {attempt} exception: {last_err}")
                await asyncio.sleep(self.retry_backoff * (2 ** (attempt - 1)))
        # Exhausted retries
        err_msg = f"LLM_FAILED: {last_err}"
        if self.context and self.context.tracer:
            self.context.tracer.log("LLM_FINAL_FAILURE", self.name, err_msg)
        return err_msg

    @abstractmethod
    async def run(self, *args, **kwargs) -> str:
        """Execute the action and return textual outcome"""
        pass

class SimpleWriteCode(Action):
    """Generate an initial Python implementation for the idea."""
    name = "SimpleWriteCode"

    async def run(self, idea: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, f"Generating code (idea length={len(idea)})")
        prompt = (
            "You are a professional programmer. Write clean, production-ready Python code.\n"
            f"Task: {idea}\n\n"
            "Requirements:\n"
            "1. Provide functions/classes with docstrings\n"
            "2. Include input validation and error handling\n"
            "3. Keep dependencies minimal\n"
            "Return only Python code."
        )
        messages = [
            {"role": "system", "content": "You are an expert Python programmer."},
            {"role": "user", "content": prompt}
        ]
        resp = await self._ask_with_retry(messages)
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, f"Generated code length={len(resp)}")
        return resp

