    name = "SimpleWriteTest"

    async def run(self, code: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, f"Generating tests (code len={len(code or '')})")
        truncated_code = (code or "")[:4000]
        prompt = (
            "You are a QA engineer. Write pytest tests for the implementation below.\n\n"
            f"Implementation:\n{truncated_code}\n\n"
            "Requirements:\n"
            "1. Provide pytest functions named test_*\n"
            "2. Cover typical, edge, and error cases\n"
            "3. Include docstrings\n"
            "Return only pytest code."
        )
        messages = [
            {"role": "system", "content": "You are an expert QA engineer."},
            {"role": "user", "content": prompt}
        ]
        resp = await self._ask_with_retry(messages)
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, f"Generated tests length={len(resp)}")
        return resp

class SimpleWriteReview(Action):
    """Review code and tests and produce concise actionable feedback."""
    name = "SimpleWriteReview"

    def __init__(self, is_human: bool = False, **kwargs):
        super().__init__(**kwargs)
        self.is_human = is_human

    async def run(self, code: str, tests: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, f"Starting review (human={self.is_human})")
        # Quick static checks
        issues: List[str] = []
        try:
            ast.parse(code or "")
        except Exception as e:
            issues.append(f"code_syntax_error: {str(e)[:160]}")
        try:
            ast.parse(tests or "")
        except Exception as e:
            issues.append(f"tests_syntax_error: {str(e)[:160]}")

        if self.is_human:
            review = "HUMAN_REVIEW: please inspect syntax and edge cases; " + "; ".join(issues) if issues else "HUMAN_REVIEW: ok"
            if self.context and self.context.tracer:
                self.context.tracer.log("ACTION_END", self.name, f"Human review produced")
            return review

        prompt = (
            "You are a senior code reviewer. Provide a concise review and a short verdict line: VERDICT: PASS or FAIL\n\n"
            f"Code (truncated):\n{(code or '')[:2000]}\n\n"
            f"Tests (truncated):\n{(tests or '')[:2000]}\n\n"
            "Focus on correctness, missing edge cases, and actionable fixes."
        )
        messages = [
            {"role": "system", "content": "You are a senior software engineer doing code review."},
            {"role": "user", "content": prompt}
        ]
        llm_review = await self._ask_with_retry(messages)
        # Merge static issues if any
        if issues:
            review = f"REVIEW_MERGED:\n{llm_review}\nSTATIC_ISSUES: {'; '.join(issues)}"
        else:
            review = llm_review
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, f"Review length={len(review)}")
        return review

class SimpleVerify(Action):
    """Perform deterministic verification: syntax, tests existence, asserts, references."""
    name = "SimpleVerify"

    async def run(self, code: str, tests: str) -> str:
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_START", self.name, "Verifying code+tests")
        status: List[str] = []
        code_ok = False
        tests_ok = False
        referenced = False
        parsed_code = None
        parsed_tests = None

        # Code syntax
        if code and code.strip():
            try:
                parsed_code = ast.parse(code)
                code_ok = True
                status.append("code_syntax: ok")
            except Exception as e:
                status.append(f"code_syntax: fail ({str(e)[:200]})")
        else:
            status.append("code_syntax: fail (empty)")

        # Tests syntax and basic assertions
        if tests and tests.strip():
            try:
                parsed_tests = ast.parse(tests)
                # Heuristic check for pytest tests or asserts
                has_test_fn = any(isinstance(n, ast.FunctionDef) and n.name.startswith("test_") for n in ast.walk(parsed_tests))
                has_assert = any(isinstance(n, ast.Assert) for n in ast.walk(parsed_tests))
                if has_test_fn or has_assert:
                    tests_ok = True
                    status.append("tests_presence: ok")
                else:
                    status.append("tests_presence: fail (no test_ functions or asserts found)")
            except Exception as e:
                status.append(f"tests_syntax: fail ({str(e)[:200]})")
        else:
            status.append("tests_presence: fail (empty)")

        # Check that tests reference functions from code
        if parsed_code and parsed_tests:
            func_names = {n.name for n in ast.walk(parsed_code) if isinstance(n, ast.FunctionDef)}
            tests_text = tests or ""
            if func_names:
                for fn in func_names:
                    if fn in tests_text:
                        referenced = True
                        break
                if referenced:
                    status.append("tests_reference: ok")
                else:
                    status.append("tests_reference: fail (no references to functions found)")
            else:
                status.append("tests_reference: warn (no top-level functions in code)")

        verified = code_ok and tests_ok and referenced
        result = f"VERIFICATION_RESULT: {'PASS' if verified else 'FAIL'} | " + "; ".join(status)
        if self.context and self.context.tracer:
            self.context.tracer.log("ACTION_END", self.name, result)
        return result

class Role(ABC):
    """
    Base Role:
    - clear responsibilities: one primary Action
    - explicit watch_list of action names that trigger the role
    - robust act(...) that returns a Message with routing metadata (send_to)
    """
    name: str = "Role"
    profile: str = "Default"
    context: Optional[Context] = None
    actions: List[Action] = []
    watch_list: List[str] = []

    def __init__(self, **kwargs):
        self.name = kwargs.get('name', self.name)
        self.profile = kwargs.get('profile', self.profile)
        self.context = kwargs.get('context')
        self.is_human = kwargs.get('is_human', False)
        self.actions = []
        self.watch_list = []
        self.env: Optional["Environment"] = None

    def set_actions(self, actions: List[Action]):
        self.actions = actions

    def _watch(self, actions: List[Type[Action]]):
        self.watch_list = [a.name for a in actions]

    async def act(self, message: Optional[Message] = None) -> Optional[Message]:
        """
        Execute the role's primary action.
        Returns a Message that includes:
        - content: textual result
        - cause_by: action.name
        - sent_from: role.name
        - send_to: set of role.profiles to route to
        """
        if not self.actions:
            return None
        action = self.actions[0]
        if self.context and self.context.tracer:
            mid = getattr(message, "id", "init")
            self.context.tracer.log("ROLE_ACT", self.name, f"Act called for message {mid} cause_by={(message.cause_by if message else 'none')}")

        try:
            # Dispatch by action name for clear mapping
            if action.name == SimpleWriteCode.name:
                idea = ""
                if message:
                    idea = getattr(message, "instruct_content", None) or message.content or ""
                result = await action.run(idea)
                # Route to Tester and Reviewer by default; if error, route back to coder/human
                send_to = {"SimpleTester", "SimpleReviewer"} if not (isinstance(result, str) and result.startswith("LLM_FAILED") or result.startswith("LLM_UNAVAILABLE") or result.startswith("Error")) else {"SimpleCoder"}

            elif action.name == SimpleWriteTest.name:
                code_text = ""
                if message:
                    code_text = message.content or ""
                else:
                    # fallback: get latest code from env
                    if self.env:
                        for msg in reversed(self.env.history):
                            if msg.cause_by == SimpleWriteCode.name:
                                code_text = msg.content
                                break
                result = await action.run(code_text)
                send_to = {"SimpleReviewer"} if not (isinstance(result, str) and result.startswith("LLM_FAILED") or result.startswith("Error")) else {"SimpleTester", "SimpleCoder"}

            elif action.name == SimpleWriteReview.name:
                # Need both code and tests; try to extract from message or env
                code_text = ""
                tests_text = ""
                if message and isinstance(message.content, str) and "CODE:" in message.content and "TESTS:" in message.content:
                    try:
                        _, rest = message.content.split("CODE:", 1)
                        code_text, tests_text = rest.split("TESTS:", 1)
                    except Exception:
                        pass
                if not code_text or not tests_text:
                    if self.env:
                        code_msg = None
                        tests_msg = None
                        for msg in reversed(self.env.history):
                            if msg.cause_by == SimpleWriteCode.name and code_msg is None:
                                code_msg = msg
                            if msg.cause_by == SimpleWriteTest.name and tests_msg is None:
                                tests_msg = msg
                            if code_msg and tests_msg:
                                break
                        code_text = code_msg.content if code_msg else code_text
                        tests_text = tests_msg.content if tests_msg else tests_text
                result = await action.run(code_text or "", tests_text or "")
                # If review indicates FAIL, route back to coder/tester; otherwise route to verifier
                lower = (result or "").lower()
                if "verdict: pass" in lower or "pass" in lower and "fail" not in lower:
                    send_to = {"SimpleVerifier"}
                else:
                    send_to = {"SimpleCoder", "SimpleTester", "SimpleVerifier"}

            elif action.name == SimpleVerify.name:
                # Collect latest code and tests
                code_text = ""
                tests_text = ""
                if self.env:
                    code_msg = None
                    tests_msg = None
                    for msg in reversed(self.env.history):
                        if msg.cause_by == SimpleWriteCode.name and code_msg is None:
                            code_msg = msg
                        if msg.cause_by == SimpleWriteTest.name and tests_msg is None:
                            tests_msg = msg
                        if code_msg and tests_msg:
                            break
                    code_text = code_msg.content if code_msg else ""
                    tests_text = tests_msg.content if tests_msg else ""
                result = await action.run(code_text, tests_text)
                # If PASS, no automatic further routing; if FAIL, route back to coder/tester
                if "VERIFICATION_RESULT: PASS" in (result or ""):
                    send_to = set()
                else:
                    send_to = {"SimpleCoder", "SimpleTester", "SimpleReviewer"}

            else:
                result = "NO_OP"
                send_to = set()

        except Exception as e:
            # Robust error handling: log and create an error message routed to coder
            err = f"ROLE_EXCEPTION: {str(e)}"
            if self.context and self.context.tracer:
                self.context.tracer.log("ROLE_EXCEPTION", self.name, err)
            return Message(content=err, role=self.profile, cause_by="RoleException", sent_from=self.name, send_to={"SimpleCoder"})

        # Build response message
        response = Message(
            content=result,
            role=self.profile,
            cause_by=action.name,
            sent_from=self.name,
            send_to=set(send_to)
        )
        if self.context and self.context.tracer:
            self.context.tracer.log("ROLE_COMPLETE", self.name, f"Produced message {getattr(response, 'id', 'none')} cause_by={response.cause_by} send_to={response.send_to}")
        return response

