class Environment:
    """
    Central environment:
    - persistent history of messages
    - message queue for processing
    - processed tracking (role_name, message_id) to avoid duplicate handling
    """
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self.message_queue: List[Message] = []
        self.processed: Set[Tuple[str, str]] = set()

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # Normalize send_to for compatibility
        if getattr(message, "send_to", None) is None:
            message.send_to = set(getattr(message, "sent_to", set()) or set())
        self.history.append(message)
        self.message_queue.append(message)
        if self.tracer:
            self.tracer.log("ENV_MESSAGE", "Environment", f"Enqueued message {getattr(message,'id','?')} from {message.sent_from} cause_by={message.cause_by} -> send_to={message.send_to}")

    def mark_processed(self, role: Role, message: Message):
        key = (role.name, getattr(message, "id", ""))
        self.processed.add(key)
        if self.tracer:
            self.tracer.log("ENV_MARK_PROCESSED", "Environment", f"Marked processed {key}")

    def has_processed(self, role: Role, message: Message) -> bool:
        return (role.name, getattr(message, "id", "")) in self.processed

    def get_relevant_roles(self, message: Message) -> List[Role]:
        """
        Determine which roles should handle this message:
        - If message.send_to explicit, route to those profiles/names
        - Else, route to roles whose watch_list contains the cause_by
        """
        targets = set(message.send_to or set())
        relevant: List[Role] = []
        if targets:
            for role in self.roles:
                if role.profile in targets or role.name in targets:
                    relevant.append(role)
            return relevant
        # fallback to watch-list routing
        for role in self.roles:
            if message.cause_by in role.watch_list:
                relevant.append(role)
        return relevant

class Team:
    """
    Orchestrator with:
    - explicit ordering to reduce race conditions
    - stable verification requirement to avoid premature termination
    - robust queue processing with safety limits
    """
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # verification stability
        self.verifier_streak = 0
        self.required_stable_passes = 2  # require 2 consecutive PASSes
        self._rounds_taken = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _run_role_on_message(self, role: Role, message: Message) -> Optional[Message]:
        """
        Execute role.act if it hasn't already processed this message.
        Mark processed immediately to avoid duplicate processing on errors.
        """
        if self.env.has_processed(role, message):
            if self.tracer:
                self.tracer.log("SKIP_PROCESSED", "Team", f"Role {role.name} already processed message {getattr(message,'id','?')}")
            return None
        # Mark as processed before execution to prevent re-entry loops
        self.env.mark_processed(role, message)
        try:
            resp = await role.act(message)
            return resp
        except Exception as e:
            # Log and publish an error message so system can continue
            err = f"ROLE_RUN_EXCEPTION: {role.name} on message {getattr(message,'id','?')} -> {str(e)}"
            if self.tracer:
                self.tracer.log("ROLE_RUN_EXCEPTION", role.name, err)
            error_msg = Message(content=err, role=role.profile, sent_from=role.name, cause_by="RoleRunException", send_to={"SimpleCoder"})
            return error_msg

    async def _process_queue_until_stable(self, max_steps: int = 300) -> bool:
        """
        Process the message queue until either verification becomes stable or the queue drains
        or max_steps is reached. Returns True if verification achieved (streak >= required_stable_passes).
        """
        steps = 0
