    async def _ask_with_retry(self, messages: List[Dict[str, str]]) -> str:
        """
        Calls the LLM with exponential backoff retry strategy.
        Returns a string response or a clear error-marked string on final failure.
        """
        last_err = None
        for attempt in range(1, self.max_llm_retries + 1):
            try:
                if self.context and self.context.tracer:
                    self.context.tracer.log("LLM_CALL", self.name, f"Attempt {attempt}/{self.max_llm_retries}")
