class Environment:
    """
    Central environment:
    - persistent history of messages
    - message queue for processing
    - processed tracking (role_name, message_id) to avoid duplicate handling
    """
    def __init__(self, tracer: Optional[ExecutionTracer] = None):
        self.roles: List[Role] = []
        self.history: List[Message] = []
        self.tracer = tracer
        self.message_queue: List[Message] = []
        self.processed: Set[Tuple[str, str]] = set()

    def add_role(self, role: Role):
        role.env = self
        self.roles.append(role)
        if self.tracer:
            self.tracer.log("ENV_ADD_ROLE", "Environment", f"Added role: {role.name} ({role.profile})")

    def publish_message(self, message: Message):
        # Normalize send_to for compatibility
        if getattr(message, "send_to", None) is None:
            message.send_to = set(getattr(message, "sent_to", set()) or set())
        self.history.append(message)
        self.message_queue.append(message)
        if self.tracer:
            self.tracer.log("ENV_MESSAGE", "Environment", f"Enqueued message {getattr(message,'id','?')} from {message.sent_from} cause_by={message.cause_by} -> send_to={message.send_to}")

    def mark_processed(self, role: Role, message: Message):
        key = (role.name, getattr(message, "id", ""))
        self.processed.add(key)
        if self.tracer:
            self.tracer.log("ENV_MARK_PROCESSED", "Environment", f"Marked processed {key}")

    def has_processed(self, role: Role, message: Message) -> bool:
        return (role.name, getattr(message, "id", "")) in self.processed

    def get_relevant_roles(self, message: Message) -> List[Role]:
        """
        Determine which roles should handle this message:
        - If message.send_to explicit, route to those profiles/names
        - Else, route to roles whose watch_list contains the cause_by
        """
        targets = set(message.send_to or set())
        relevant: List[Role] = []
        if targets:
            for role in self.roles:
                if role.profile in targets or role.name in targets:
                    relevant.append(role)
            return relevant
        # fallback to watch-list routing
        for role in self.roles:
            if message.cause_by in role.watch_list:
                relevant.append(role)
        return relevant

class Team:
    """
    Orchestrator with:
    - explicit ordering to reduce race conditions
    - stable verification requirement to avoid premature termination
    - robust queue processing with safety limits
    """
    def __init__(self, context: Optional[Context] = None, log_file: Optional[str] = None):
        self.context = context or Context()
        self.tracer = ExecutionTracer(log_file)
        self.context.tracer = self.tracer
        self.env = Environment(self.tracer)
        self.investment: float = 20.0
        self.idea: str = ""
        self.log_file = log_file
        # verification stability
        self.verifier_streak = 0
        self.required_stable_passes = 2  # require 2 consecutive PASSes
        self._rounds_taken = 0

    def hire(self, roles: List[Role]):
        for role in roles:
            role.context = self.context
            role.env = self.env
            self.env.add_role(role)

    def invest(self, investment: float):
        self.investment = investment

    def run_project(self, idea: str):
        self.idea = idea
        self.tracer.log("TEAM_START", "Team", f"Starting project: {idea}")

    async def _run_role_on_message(self, role: Role, message: Message) -> Optional[Message]:
        """
        Execute role.act if it hasn't already processed this message.
        Mark processed immediately to avoid duplicate processing on errors.
        """
        if self.env.has_processed(role, message):
            if self.tracer:
                self.tracer.log("SKIP_PROCESSED", "Team", f"Role {role.name} already processed message {getattr(message,'id','?')}")
            return None
        # Mark as processed before execution to prevent re-entry loops
        self.env.mark_processed(role, message)
        try:
            resp = await role.act(message)
            return resp
        except Exception as e:
            # Log and publish an error message so system can continue
            err = f"ROLE_RUN_EXCEPTION: {role.name} on message {getattr(message,'id','?')} -> {str(e)}"
            if self.tracer:
                self.tracer.log("ROLE_RUN_EXCEPTION", role.name, err)
            error_msg = Message(content=err, role=role.profile, sent_from=role.name, cause_by="RoleRunException", send_to={"SimpleCoder"})
            return error_msg

    async def _process_queue_until_stable(self, max_steps: int = 300) -> bool:
        """
        Process the message queue until either verification becomes stable or the queue drains
        or max_steps is reached. Returns True if verification achieved (streak >= required_stable_passes).
        """
        steps = 0
        verified = False
        while self.env.message_queue and steps < max_steps:
            steps += 1
            message = self.env.message_queue.pop(0)
            if self.tracer:
                self.tracer.log("QUEUE_DEQUEUE", "Team", f"Dequeued {getattr(message,'id','?')} cause_by={message.cause_by}")
            relevant_roles = self.env.get_relevant_roles(message)
            if not relevant_roles:
                # Broadcast to watchers as fallback
                relevant_roles = [r for r in self.env.roles if message.cause_by in r.watch_list]
            for role in relevant_roles:
                resp = await self._run_role_on_message(role, message)
                if resp:
                    # Publish role response
                    self.env.publish_message(resp)
                    # Track verification streak if verifier generated a verification result
                    if resp.cause_by == SimpleVerify.name and "VERIFICATION_RESULT: PASS" in (resp.content or ""):
                        self.verifier_streak += 1
                        if self.tracer:
                            self.tracer.log("VERIFIER_STREAK", "Team", f"Streak incremented to {self.verifier_streak}")
                    elif resp.cause_by == SimpleVerify.name:
                        # reset on fail or inconclusive
                        if self.tracer:
                            self.tracer.log("VERIFIER_RESET", "Team", "Verification failed or inconclusive; resetting streak")
                        self.verifier_streak = 0
                    # Early acceptance if streak achieved
                    if self.verifier_streak >= self.required_stable_passes:
                        if self.tracer:
                            self.tracer.log("VERIFY_SUCCESS", "Team", f"Verification stable: {self.verifier_streak} consecutive PASSes")
                        return True
            # small yield to allow cooperative scheduling
            await asyncio.sleep(0)
        if steps >= max_steps:
            if self.tracer:
                self.tracer.log("QUEUE_MAX_STEPS", "Team", f"Reached max processing steps ({max_steps}) without stable verification")
        return verified

    async def run(self, n_round: int = 4):
        """
        Main orchestration loop:
        - publish initial idea targeted to coder
        - process queue to stability each round
        - nudge coder if queue drains without progress
        - stop when verification is stable (required_stable_passes)
        """
        self.tracer.log("TEAM_RUN", "Team", f"Running up to {n_round} rounds (stable_passes={self.required_stable_passes})")
        initial_msg = Message(
            content=f"Let's work on this project: {self.idea}",
            instruct_content=self.idea,
            role="Human",
            sent_from="User",
            cause_by="UserInput",
            send_to={"SimpleCoder"}
        )
        self.env.publish_message(initial_msg)

        verified = False
        for round_num in range(n_round):
            self._rounds_taken = round_num + 1
            self.tracer.log("ROUND_START", "Team", f"Round {round_num + 1}/{n_round}")
            # Process until stable or queue drained
            await self._process_queue_until_stable(max_steps=300)
            self.tracer.log("ROUND_END", "Team", f"Round {round_num + 1} completed; verifier_streak={self.verifier_streak}; history_len={len(self.env.history)}")
            # Check if stable verification achieved
            if self.verifier_streak >= self.required_stable_passes:
                self.tracer.log("TEAM_EARLY_STOP", "Team", f"Verification stable after {round_num+1} rounds")
                verified = True
                break
            # If queue drained and no verification, nudge coder once per round
            if not self.env.message_queue:
                last_user = next((m for m in reversed(self.env.history) if m.cause_by == "UserInput"), None)
                if last_user:
                    nudged = Message(
                        content=f"Please refine the implementation for: {self.idea}",
                        instruct_content=self.idea,
                        role="System",
                        sent_from="Orchestrator",
                        cause_by="Nudge",
                        send_to={"SimpleCoder"}
                    )
                    self.env.publish_message(nudged)
                    # process nudged messages in this round
                    await self._process_queue_until_stable(max_steps=150)
                    if self.verifier_streak >= self.required_stable_passes:
                        verified = True
                        break
            # allow next round to proceed
        # Finalization
        self.tracer.log("TEAM_END", "Team", "Project completed")
        summary = f"Project '{self.idea}' completed after {self._rounds_taken} rounds with {len(self.env.history)} messages exchanged. verifier_streak={self.verifier_streak}"
        self.tracer.log("SUMMARY", "Team", summary)

